import logo from './logo.svg';
import './App.css';

import {useState} from 'react';
const Header = () => {
  const [isOpen, setMenu] = useState(false);
  const toggleMenu = () => {
    sdtMenu(isOpen => ~isOpen);
  }
  return(
    <div className="header">
            <ul className="header-wrapper">
                <li><MenuIcon onClick={()=>toggleMenu()}></MenuIcon></li>  // 클릭되었을 때 준비한 함수 호출! on off 개념                     
                <li><img width="45px" src="https://www.burgerking.co.kr/dist/img/logo_titleBar.e89f6852.png"></img></li>
                <li>
                    <Button
                        variant="contained"
                        color="secondary"
                        size="small"
                        style={buttonStyles}
                    >
                        딜리버리주문 >
                    </Button>
                </li>
            </ul>
            <ul className={isOpen ? "show-menu" : "hide-menu"}> // 삼항연산자 true 일 때 클래스명 show-menu, false일때 hide-menu
                    <li >1</li>
                    <li >2</li>
                    <li >3</li>
                    <li >4</li>
            </ul>
    </div>
)
}

function App() {

  return (

    <div className="App">
      <div className="pink">
        <h4>yealin</h4>
      </div>
      <div>

      </div>

    </div>
  );
}

export default App;
