    const [isOpen, setMenu] = useState(false);
    const toggleMenu = () => {
      sdtMenu(isOpen => ~isOpen);
    }
    return(
      <div className="header">
              <ul className="header-wrapper">
                  <li><MenuIcon onClick={()=>toggleMenu()}></MenuIcon></li>  // 클릭되었을 때 준비한 함수 호출! on off 개념                     
