                  </li>
              </ul>
              <ul className={isOpen ? "show-menu" : "hide-menu"}> // 삼항연산자 true 일 때 클래스명 show-menu, false일때 hide-menu
                      <li >1</li>
                      <li >2</li>
                      <li >3</li>
                      <li >4</li>
              </ul>
      </div>
  )
  }
}

