import './App.css';
import { useState } from 'react';

function App() {

  let [글제목, 글제목변경] = useState(['배우고 싶은 언어(기술)', '나의 소개', '이름:', '학번:'])
  const [isClick, setClick] =useState(false);


  return (
      <div className="App">

        <div className="pink">
          <h4>yealin</h4>
        </div>




          <button onClick={ ()=>{
            setClick(!isClick);
          } }>나의 소개</button>
          {isClick? <span>이름: 김예린<br/>학번: 20223520
            <br/>학과: 컴퓨터공학과<br/>취미: 그림</span>:
            <span>나의 소개</span>}

        <div className="list">
          <h4>{글제목[0]}</h4>
        </div>
        <div className="list">
          <h4>{글제목[1]}</h4>
        </div>



      </div>
  );
}

export default App;
